import React from 'react';
import { Grid, Card, CardContent, CardMedia, Typography, Box } from '@mui/material';

const products = [
    {
        id: 1,
        imageUrl: 'https://pavaotogo.com/wp-content/uploads/2020/05/Pavao-Meats-Groceries-30-Egg-Carton.jpg',
        title: 'Cartón de 30 huevos',
        price: '$20.00'
    },
    {
        id: 2,
        imageUrl: 'https://www.farmtek.com/media/catalog/product/cache/f9d76ab52b9381a4c660f4521b58327f/1/1/117760b.jpg',
        title: 'Paquete de 18 huevos',
        price: '$30.00'
    },
    {
        id: 3,
        imageUrl: 'https://pavaotogo.com/wp-content/uploads/2020/05/Pavao-Meats-Groceries-30-Egg-Carton.jpg',
        title: 'Descuento si compra en cantidades',
    },
];

const Product = () => {
    return (
        <Box
            sx={{
                minHeight: 'calc(100vh - 128px)',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                px: { xs: 2, md: 4 },
                py: 4,
                gap: 6,
            }}
        >
            {/* Product Grid Section */}
            <Box sx={{ width: '100%' }}>
                <Grid
                    container
                    spacing={12}
                    justifyContent="center"
                >
                    {products.map((product) => (
                        <Grid
                            item
                            key={product.id}
                            xs={12}
                            sm={6}
                            md={4}
                            lg={3}
                            sx={{
                                display: 'flex',
                                justifyContent: 'center',
                            }}
                        >
                            <Card
                                sx={{
                                    width: 300,
                                    height: 300,
                                    display: 'flex',
                                    flexDirection: 'column',
                                    justifyContent: 'space-between',
                                    alignItems: 'center',
                                    boxShadow: 3,
                                    p: 2,
                                    textAlign: 'center',
                                }}
                            >
                                <CardMedia
                                    component="img"
                                    image={product.imageUrl}
                                    alt={product.title}
                                    sx={{
                                        maxHeight: 160,
                                        width: 'auto',
                                        objectFit: 'contain',
                                        mb: 2,
                                    }}
                                />
                                <CardContent
                                    sx={{
                                        flexGrow: 1,
                                        display: 'flex',
                                        flexDirection: 'column',
                                        alignItems: 'center',
                                        justifyContent: 'space-between',
                                        gap: 2,
                                        width: '100%',
                                        p: 0,
                                    }}
                                >
                                    <Typography variant="h6">{product.title}</Typography>
                                    {product.price && (
                                        <Typography
                                            variant="h6"
                                            color="primary"
                                            sx={{ mt: 'auto' }}
                                        >
                                            {product.price}
                                        </Typography>
                                    )}
                                </CardContent>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            </Box>

            {/* QR Payment Section */}
            <Box sx={{ textAlign: 'center', mt: 4 }}>
                <Typography variant="h6" gutterBottom>
                    Pague aquí con ATH Mobil
                </Typography>
                <Box
                    sx={{
                        display: 'flex',
                        justifyContent: 'center',
                        gap: 20,
                        flexWrap: 'wrap',
                        mt: 2,
                    }}
                >
                    <Box
                        component="img"
                        title='Aida'
                        src="/qr1.png"
                        alt="Código QR de Aida"
                        sx={{ width: 120, height: 120 }}
                    />
                    <Box
                        component="img"
                        src="/qr2.png"
                        alt="Código QR de Luis"
                        sx={{ width: 120, height: 120 }}
                    />
                </Box>
            </Box>
        </Box>
    );
};

export default Product;
