import React from 'react';
import { Box } from '@mui/material';
import Footer from './Footer';  // Assuming you already have this component
import Header from './Header';  // Assuming you already have this component

const Layout = ({ children }) => {
  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        height: '100vh', // Full viewport height
      }}
    >
      {/* Header Section */}
      <Header />

      {/* Scrollable Content Area */}
      <Box
        sx={{
          flex: 1,
          overflowY: 'auto', // Makes content scrollable
          pt: '60px', // Adds padding below header (adjust based on header height)
        }}
      >
        {children}
      </Box>

      {/* Footer Section */}
      <Footer />
    </Box>
  );
};

export default Layout;
