import React from 'react';
import { Box, Grid, Typography, Container } from '@mui/material';

const contentItems = [
    {
        id: 1,
        imageUrl: '',
        title: 'Productos frescos',
        description: 'Ofrecemos productos frescos directamente de productores locales.'
    },
    {
        id: 2,
        imageUrl: 'https://unsplash.com/photos/burger-skewered-with-knife-near-black-textile-1Shk_PkNkNw',
        title: 'Carnes de calidad',
        description: 'Carnes seleccionadas con altos estándares de frescura y sabor.'
    },
    {
        id: 3,
        imageUrl: 'https://unsplash.com/photos/black-and-white-new-balance-low-top-sneaker-6zO5VKogoZE',
        title: 'Frutas naturales',
        description: 'Frutas orgánicas para una alimentación saludable y deliciosa.'
    },
    {
        id: 4,
        imageUrl: 'https://source.unsplash.com/random/800x600?vegetables',
        title: 'Vegetales verdes',
        description: 'Una gran variedad de vegetales recién cosechados.'
    },
    {
        id: 5,
        imageUrl: 'https://source.unsplash.com/random/800x600?bakery',
        title: 'Panadería artesanal',
        description: 'Pan fresco horneado cada día en nuestra panadería local.'
    },
    {
        id: 6,
        imageUrl: 'https://source.unsplash.com/random/800x600?eggs',
        title: 'Huevos de granja',
        description: 'Cartones de huevos frescos y grandes, directamente de la granja.'
    },
    {
        id: 7,
        imageUrl: 'https://source.unsplash.com/random/800x600?cheese',
        title: 'Variedad de quesos',
        description: 'Quesos nacionales e importados de la mejor calidad.'
    },
    {
        id: 8,
        imageUrl: 'https://source.unsplash.com/random/800x600?snacks',
        title: 'Snacks y botanas',
        description: 'Gran surtido de snacks para toda la familia.'
    },
    {
        id: 9,
        imageUrl: 'https://source.unsplash.com/random/800x600?spices',
        title: 'Especias y condimentos',
        description: 'Todo lo que necesitas para darle sabor a tu cocina.'
    },
    {
        id: 10,
        imageUrl: 'https://source.unsplash.com/random/800x600?beverages',
        title: 'Bebidas refrescantes',
        description: 'Jugos, aguas y refrescos para toda ocasión.'
    },
];

const Home = () => {
    return (
        <Container maxWidth="lg" sx={{ py: 4 }}>
            {contentItems.map((item) => {
                const isOdd = item.id % 2 !== 0;

                return (
                    <Grid
                        container
                        spacing={4}
                        alignItems="center"
                        justifyContent="center"
                        key={item.id}
                        direction={{ xs: 'column', md: isOdd ? 'row-reverse' : 'row' }}
                        sx={{ mb: 6 }}
                    >
                        <Grid item xs={12} md={6}>
                            <Box
                                component="img"
                                src={item.imageUrl}
                                alt={item.title}
                                sx={{
                                    width: '100%',
                                    height: 300,
                                    objectFit: 'cover',
                                    borderRadius: 2,
                                    boxShadow: 3,
                                }}
                            />
                        </Grid>
                        <Grid item xs={12} md={6}>
                            <Typography variant="h5" gutterBottom>
                                {item.title}
                            </Typography>
                            <Typography variant="body1" color="text.secondary">
                                {item.description}
                            </Typography>
                        </Grid>
                    </Grid>
                );
            })}
        </Container>
    );
};

export default Home;
