import React from 'react';
import { Grid, Card, CardContent, CardMedia, Typography } from '@mui/material';

// Sample product data for demonstration
const products = [
    {
        id: 1,
        imageUrl: 'https://pavaotogo.com/wp-content/uploads/2020/05/Pavao-Meats-Groceries-30-Egg-Carton.jpg',
        title: 'Product 1',
        description: 'Description of Product 1.',
        price: '$20.00'
    },
    {
        id: 2,
        imageUrl: 'https://via.placeholder.com/300',
        title: 'Product 2',
        description: 'Description of Product 2.',
        price: '$30.00'
    },
    {
        id: 3,
        imageUrl: 'https://via.placeholder.com/300',
        title: 'Product 3',
        description: 'Description of Product 3.',
        price: '$40.00'
    },
    // Add more products as needed
];

const Gallery = () => {
    return (
        <Grid container spacing={2} sx={{ padding: 2, justifyContent: 'center' }}>
            {products.map((product) => (
                <Grid
                    item
                    xs={12} sm={6} md={4} lg={3} // Responsive breakpoints
                    key={product.id}
                    sx={{
                        display: 'flex',
                        justifyContent: 'center', // Center horizontally
                        alignItems: 'center', // Center vertically
                    }}
                >
                    <Card sx={{ maxWidth: 345, display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
                        <CardMedia
                            component="img"
                            height="140"
                            image={product.imageUrl}
                            alt={product.title}
                        />
                        <CardContent sx={{ textAlign: 'center' }}>
                            <Typography variant="h6" gutterBottom>
                                {product.title}
                            </Typography>
                            <Typography variant="body2" color="text.secondary">
                                {product.description}
                            </Typography>
                            <Typography variant="h6" color="primary" sx={{ marginTop: 1 }}>
                                {product.price}
                            </Typography>
                        </CardContent>
                    </Card>
                </Grid>
            ))}
        </Grid>
    );
};

export default Gallery;
